import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Application } from '../model/application.model';
import { Technology } from '../model/technology.model';
import { FormBuilder, Validators } from '@angular/forms';
import { formatDate } from '@angular/common';
import { TechnologyService } from '../service/technology/technology.service';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {
  @Input("application") application: Application | null = null;
  @Input("technologies") technologies: Array<Technology> = [];
  @Output() eventEmitter = new EventEmitter<any>();
  applicationForm = this.fb.group({
    description: ["", Validators.maxLength(300)],
    email: ["", [Validators.required, Validators.email]],
    neededTill: [formatDate(new Date(), "yyyy-MM-dd HH:mm", "en"), Validators.required],
    newTechnology: [false],
    technology: this.fb.group({
      type: ["", Validators.required],
      ram: [4, Validators.required],
      cores: [1, Validators.required],
      motherboard: ["", Validators.required],
      gpu: ["", Validators.required],
    }),
  });

  technologyIdSelected = -1;
  motherboards : Array<string> = [];
  gpus : Array<string> = [];

  constructor(private fb: FormBuilder, private technologyService: TechnologyService) {}

  ngOnInit(): void {
    // This means that we are editing an application
    if (this.application) {
      this.applicationForm.patchValue({
        description: this.application.description,
        email: this.application.email,
        neededTill: formatDate(new Date(this.application.neededTill), "yyyy-MM-dd HH:mm", "en"),
        newTechnology: this.application.technology ? true : false,
        technology: {
          type: this.application.technology?.type,
          ram: this.application.technology?.ram,
          cores: this.application.technology?.cores,
          motherboard: this.application.technology?.motherboard,
          gpu: this.application.technology?.gpu,
        }
      });
    }
    this.initArrays();
  }

  get controls() {return this.applicationForm.controls;}
  get techControls() {return this.controls.technology.controls;}

  get technologyTypes() { return this.technologyService.technologyTypes;}

  get technologyRAM() { return this.technologyService.technologyRAM;}

  get technologyCores() { return this.technologyService.technologyCores;}

  initArrays() {
    const type = this.controls.technology.value.type ? this.controls.technology.value.type : "desktop";
    this.motherboards = this.technologyService.technologyMotherboards(type);
    this.gpus = this.technologyService.technologyGPU(type);
  }

  onCheckboxChange(event: any) {
    this.controls.newTechnology.setValue(event.target.checked);
    if (event.target.checked) {
      this.technologyIdSelected = -1;
      this.applicationForm.patchValue({
        technology: {
          type: "desktop",
          ram: 4,
          cores: 1,
          motherboard: "motherboard_desktop_1",
          gpu: "gpu_desktop_1",
        }
      });
    } else {
      this.applicationForm.patchValue({
        technology: {
          type: this.application?.technology?.type ? this.application?.technology?.type : "desktop",
          ram: this.application?.technology?.ram,
          cores: this.application?.technology?.cores,
          motherboard: this.application?.technology?.motherboard,
          gpu: this.application?.technology?.gpu,
        }
      });
      this.initArrays();
    }
  }

  onTechnologyClicked(id: number) {
    if (this.technologyIdSelected != id) {
      this.technologyIdSelected = id;
      const tech = this.technologies.find(t => t.id === id);
      this.applicationForm.patchValue({
        technology: {
          type: tech?.type,
          ram: tech?.ram,
          cores: tech?.cores,
          motherboard: tech?.motherboard,
          gpu: tech?.gpu,
        }
      });
    }
  }

  onTypeChange(event: any) {
    this.motherboards = this.technologyService.technologyMotherboards(event.target.value)
    this.gpus = this.technologyService.technologyGPU(event.target.value)
  }

  onSubmit() {
    if (this.applicationForm.valid) {
      const newApplication: any = this.applicationForm.value;
      if (this.application != null) {
        // This is edit
        newApplication.id = this.application.id;
        newApplication.created = this.application.created;
        newApplication.technology.id = this.application.technology?.id;
        newApplication.status = this.application.status;
        console.log(newApplication);
      } else {
        newApplication.id = 0;
        newApplication.created = new Date().getMilliseconds();
        newApplication.status = "sent";
        newApplication.technology.id = 0;
      }
    }
  }

  emitEvent() {
    this.application = null;
    this.applicationForm.reset();
    this.eventEmitter.emit();
  }
}
<div>
  <h3 *ngIf="application">Edit application</h3>
  <h3 *ngIf="!application">Apply</h3>
  <form [formGroup]="applicationForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label class="form-label" for="description">Description:</label>
      <textarea
        type="text"
        class="form-control"
        id="description"
        formControlName="description"
        aria-describedby="help"
        maxlength="300"
      ></textarea>
      <div id="help" class="form-text">Why do you need this? No more than 300 characters.</div>
    </div>

    <div class="form-group">
      <label class="form-label" for="email">Email:</label>
      <input
        type="text"
        class="form-control"
        id="email"
        formControlName="email"
        placeholder="something@mail.com"
      />

      <div
        class="alert alert-danger"
        *ngIf="controls.email?.invalid && (controls.email?.touched || controls.email?.dirty)"
      >
        <div *ngIf="controls.email?.errors?.['required']">
          Email can't be empty.
        </div>
        <div *ngIf="controls.email?.errors?.['email']">
          Input a valid email.
        </div>
      </div>
    </div>

    <div class="form-group">
      <label class="form-label" for="neededTill">Needed till:</label>
      <input
        type="datetime-local"
        class="form-control"
        id="neededTill"
        formControlName="neededTill"
      />
      <div
        class="alert alert-danger"
        *ngIf="controls.neededTill?.invalid && (controls.neededTill?.touched || controls.neededTill?.dirty)"
      >
        <div *ngIf="controls.neededTill?.errors?.['required']">
          Input a valid date.
        </div>
      </div>
    </div>

    <div class="form-check form-switch mt-2">
      <input
        class="form-check-input"
        type="checkbox"
        formControlName="newTechnology"
        id="switch"
        [value]="controls.newTechnology.value"
        (change)="onCheckboxChange($event)"
      >
      <label class="form-check-label" for="switch">Request a new technology?</label>
    </div>

    <div *ngIf="controls.newTechnology.value" class="mt-2">
      <div class="form-group">
        <label class="form-label" for="techType">Type:</label>
        <select class="form-select" id="techType" (change)="onTypeChange($event)">
          <option
            *ngFor="let type of technologyTypes"
            [value]="type"
            [selected]="controls.technology.value.type == type"
          >{{type}}</option>
        </select>

        <div
          class="alert alert-danger"
          *ngIf="techControls.type?.invalid && (techControls.type?.touched || techControls.type?.dirty)"
        >
          <div *ngIf="techControls.type?.errors?.['required']">
            Type is required.
          </div>
        </div>
      </div>

      <div class="form-group">
        <label class="form-label" for="techRAM">RAM (GB):</label>
        <select class="form-select" id="techRAM">
          <option
            *ngFor="let r of technologyRAM"
            [value]="r"
            [selected]="controls.technology.value.ram == r"
          >{{r}}</option>
        </select>

        <div
          class="alert alert-danger"
          *ngIf="techControls.ram?.invalid && (techControls.ram?.touched || techControls.ram?.dirty)"
        >
          <div *ngIf="techControls.ram?.errors?.['required']">
            RAM is required.
          </div>
        </div>
      </div>

      <div class="form-group">
        <label class="form-label" for="techCores">Cores:</label>
        <select class="form-select" id="techCores">
          <option
            *ngFor="let c of technologyCores"
            [value]="c"
            [selected]="controls.technology.value.cores == c"
          >{{c}}</option>
        </select>

        <div
          class="alert alert-danger"
          *ngIf="techControls.cores?.invalid && (techControls.cores?.touched || techControls.cores?.dirty)"
        >
          <div *ngIf="techControls.cores?.errors?.['required']">
            Cores is required.
          </div>
        </div>
      </div>

      <div class="form-group">
        <label class="form-label" for="techMotherboard">Motherboard:</label>
        <select class="form-select" id="techMotherboard">
          <option
            *ngFor="let mb of motherboards"
            [value]="mb"
            [selected]="controls.technology.value.motherboard == mb"
          >{{mb}}</option>
        </select>

        <div
          class="alert alert-danger"
          *ngIf="techControls.motherboard?.invalid && (techControls.motherboard?.touched || techControls.motherboard?.dirty)"
        >
          <div *ngIf="techControls.motherboard?.errors?.['required']">
            Motherboard is required.
          </div>
        </div>
      </div>

      <div class="form-group">
        <label class="form-label" for="techGPU">GPU:</label>
        <select class="form-select" id="techGPU">
          <option
            *ngFor="let gpu of gpus"
            [value]="gpu"
            [selected]="controls.technology.value.gpu == gpu"
          >{{gpu}}</option>
        </select>

        <div
          class="alert alert-danger"
          *ngIf="techControls.gpu?.invalid && (techControls.gpu?.touched || techControls.gpu?.dirty)"
        >
          <div *ngIf="techControls.gpu?.errors?.['required']">
            GPU is required.
          </div>
        </div>
      </div>
    </div>

    <div *ngIf="!controls.newTechnology.value" class="mt-2">
      <table class="table table-hover table-responsive">
        <thead class="table-dark">
          <tr>
            <th scope="col">Type</th>
            <th scope="col">RAM (GB)</th>
            <th scope="col">Cores</th>
            <th scope="col">Motherboard</th>
            <th scope="col">GPU</th>
          </tr>
        </thead>
        <tbody>
          <tr 
            *ngFor="let t of technologies"
            (click)="onTechnologyClicked(t.id)"
            [ngStyle]="{'background-color': (t.id == technologyIdSelected) ? '#D3D3D3' : '#ffffff'}"
          >
            <td>{{t.type}}</td>
            <td>{{t.ram}}</td>
            <td>{{t.cores}}</td>
            <td>{{t.motherboard}}</td>
            <td>{{t.gpu}}</td>
          </tr>
        </tbody>
      </table>
    </div>

    <div class="d-flex justify-content-around">
      <button
        type="submit"
        class="btn btn-primary my-4"
        [disabled]="!applicationForm.valid"
      >Submit</button>
      <button
        class="btn btn-outline-danger my-4"
        (click)="emitEvent()"
      >Cancel</button>
    </div>
  </form>
</div>
