import { Component, OnInit } from '@angular/core';
import { ApplicationService } from '../service/application/application.service';
import { Application } from '../model/application.model';
import { Technology } from '../model/technology.model';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css']
})
export class IndexComponent implements OnInit {
  title = "Application management system";
  applications: Array<Application> = [];
  application: Application | null = null;
  technologies: Array<Technology> = [];
  showForm = false;
  loading = true;

  constructor(private applicationService: ApplicationService) {}

  ngOnInit(): void {
    try {
      this.getAllApplications();
    } catch (error) {
      console.log(error);
    } finally {
      this.loading = false;
    }
  }

  handleDeleteEvent(a: Application) {
    this.applicationService.deleteApplication(a.id).subscribe((response: any) => {
      if (response.status === 200) {
        this.getAllApplications();
      }
    });
  }

  handleFormEvent(a: any | null) {
    if (a) {
      this.applicationService.saveApplication(a).subscribe((response: any) => {
        this.getAllApplications();
        if (response.status === 201) {
          console.log
          this.toggleForm();
        }
    });
    } else {
      this.toggleForm();
    }
  }

  toggleForm() {this.showForm = !this.showForm;}

  private getAllApplications() {
    this.applicationService.getAllApplications().subscribe((response: any) => {
      if (response.status === 200 && response?.body?.data) {
        for (let a of response.body.data) {
          const tech = a?.technology;
          let technology = null;
          if (tech) {
            technology = Technology.toModel(tech);
          }
          const application: Application = Application.toModel(a, technology);

          this.applications.push(application);
        }
      }
    });
  }
}
<div class="w-fill h-full p-3">
  <div class="d-flex justify-content-between">
    <h1>{{title}}</h1>
    <i class="bi bi-plus-square-fill fa-5x my-auto" *ngIf="!showForm" (click)="toggleForm()"></i>
  </div>
  <div class="d-flex">
    <app-table
      *ngIf="!loading"
      class="w-100"
      [applications]="applications"
      [showForm]="showForm"
      (actionEmitter)="handleDeleteEvent($event)"
    ></app-table>
    <app-form
      class="w-75 ms-3"
      *ngIf="showForm"
      (eventEmitter)="handleFormEvent($event)"
    ></app-form>
  </div>
</div>
