import { Component, Input } from '@angular/core';
import { Application } from '../model/application.model';
import { Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})
export class TableComponent {
  @Input() applications: Array<Application> = [];
  @Input() showForm: Array<Application> = [];
  @Output() actionEmitter = new EventEmitter<any>();

  toDate(value: number) : Date {
    return new Date(value);
  }

  getDescription(value: any) : string {
    return value ? value : "-";
  }

  emitEvent(isEdit: boolean, application: Application) {
    this.actionEmitter.emit({isEdit: isEdit, application: application});
  }
}
<div>
  <table class="table table-striped table-hover table-responsive">
    <thead class="table-dark">
      <tr>
        <th scope="col">#</th>
        <th scope="col">Description</th>
        <th scope="col">Email</th>
        <th scope="col">Created</th>
        <th scope="col">Needed till</th>
        <th scope="col">Technology</th>
        <th scope="col">Status</th>
        <th scope="col">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let a of applications">
        <th scope="row">{{a.id}}</th>
        <td>{{getDescription(a?.description)}}</td>
        <td>{{a.email}}</td>
        <td>{{toDate(a.created) | date: 'dd.MM.yyyy HH:mm'}}</td>
        <td>{{toDate(a.neededTill) | date: 'dd.MM.yyyy HH:mm'}}</td>
        <td>{{a.technology?.type}}</td>
        <td>{{a.status}}</td>
        <td class="d-flex">
          <button class="btn btn-primary w-100" (click)="emitEvent(true, a)">Edit</button>
          <span class="mx-1"></span>
          <button class="btn btn-danger w-100" (click)="emitEvent(false, a)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>

</div>
