import { Component, OnInit } from '@angular/core';
import { ApplicationService } from '../service/application/application.service';
import { Application } from '../model/application.model';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css']
})
export class IndexComponent implements OnInit {
  title = "Application management system";
  applications: Array<Application> = [];

  constructor(private applicationService: ApplicationService) {}

  ngOnInit(): void {
    this.getAllApplications();
  }

  private getAllApplications() {
    this.applicationService.getAllApplications().subscribe((response: any) => {
      if (response.status === 200 && response?.body?.data) {
        console.log(response.body.data);
        for (let application of response.body.data) {
          const tech = application?.technology;
          let technology: Technology;
          if (tech) {
            technology = new Technology(tech.id, tech.type, tech.ram, tech.cores, tech.motherboard, tech.gpu);
          }
          this.applications.push(application);
        }
      }
    });
  }
}
