import { Component, OnInit } from '@angular/core';
import { ApplicationService } from '../service/application/application.service';
import { Application } from '../model/application.model';
import { Technology } from '../model/technology.model';
import { TechnologyService } from '../service/technology/technology.service';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css']
})
export class IndexComponent implements OnInit {
  title = "Application management system";
  applications: Array<Application> = [];
  technologies: Array<Technology> = [];
  showForm = false;
  loading = false;

  constructor(private applicationService: ApplicationService, private technologyService: TechnologyService) {}

  ngOnInit(): void {

    this.getAllApplications();
    this.getAllTechnologies();
  }

  handleActionEvent(event: any) {
    console.log(event);
  }

  toggleForm() {
    this.showForm = !this.showForm;
  }

  private getAllApplications() {
    this.applicationService.getAllApplications().subscribe((response: any) => {
      if (response.status === 200 && response?.body?.data) {
        for (let a of response.body.data) {
          const tech = a?.technology;
          let technology = null;
          if (tech) {
            technology = Technology.toModel(tech);
          }
          const application: Application = Application.toModel(a, technology);

          this.applications.push(application);
        }
      }
    });
  }

  private getAllTechnologies() {
    this.technologyService.getAllTechnologies().subscribe((response: any) => {
      if (response.status === 200 && response?.body?.data) {
        for (let t of response.body.data) {
          const technology = Technology.toModel(t);
          this.technologies.push(technology);
          console.log(technology);
        }
      }
    });
  }
}
<div class="w-fill h-full p-3">
  <div class="d-flex justify-content-between">
    <h1>{{title}}</h1>
    <i class="bi bi-plus-square-fill fa-5x my-auto"></i>
  </div>
  <div class="d-flex">
    <app-table
      class="w-100"
      [applications]="applications"
      (actionEmitter)="handleActionEvent($event)"
    ></app-table>
    <app-form
      class="w-50"
      *ngIf="showForm"
    ></app-form>
  </div>
</div>
