import { Component, Input, OnInit } from '@angular/core';
import { Application } from '../model/application.model';
import { Technology } from '../model/technology.model';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {
  @Input("application") application: Application | null = null;
  @Input("technologies") technologies: Array<Technology> = [];
  technologyForm: FormGroup = new FormGroup({
      type: new FormControl(''),
      ram: new FormControl(4),
      cores: new FormControl(1),
      motherboard: new FormControl(''),
      gpu: new FormControl(''),
    });
  applicationForm : FormGroup = new FormGroup({
    description: new FormControl(''),
    email: new FormControl(''),
    neededTill: new FormControl(''),
    newTechnology: new FormControl(false),
    technology: this.technologyForm,
  });

  constructor() {}

  ngOnInit(): void {
    // This means that we are editing an application
    if (this.application) {
      this.applicationForm.patchValue(this.application);
      // this.applicationForm.controls["description"].setValue(this.application.description);
      // this.applicationForm.controls["email"].setValue(this.application.email);
      // this.applicationForm.controls["neededTill"].setValue(this.application.neededTill);
      // if (this.application.technology) {
      //   this.applicationForm.controls["newTechnology"].setValue(true);
      //   this.technologyForm.controls["type"].setValue(this.application.technology.type);
      //   this.technologyForm.controls["ram"].setValue(this.application.technology.ram);
      //   this.technologyForm.controls["cores"].setValue(this.application.technology.cores);
      //   this.technologyForm.controls["motherboard"].setValue(this.application.technology.motherboard);
      //   this.technologyForm.controls["gpu"].setValue(this.application.technology.gpu);
      // }
    }
  }

}
<div>
  <h3 *ngIf="application">Edit application</h3>
  <h2 *ngIf="!application">Apply</h2>
  <form formGroupName="applicationForm">
    <div class="form-group">
      <label
        class="form-label"
        for="description"
      >Description:</label>
      <textarea
        type="text"
        class="form-control"
        id="description"
        formControlName="description"
        aria-describedby="help"
      ></textarea>
      <div id="help" class="form-text">Why do you need this?</div>
    </div>
  </form>
</div>
