import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Application } from '../model/application.model';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})
export class TableComponent {
  @Input("applications") applications: Array<Application> = [];
  @Input("showForm") showForm: boolean = false;
  @Output() actionEmitter = new EventEmitter<any>();

  toDate(value: number) : Date {
    return new Date(value);
  }

  getDescription(value: any) : string {
    return value ? value : "-";
  }

  emitEvent(action: string, application: Application) {
    this.actionEmitter.emit({action: action});
  }
}
<div class="my-3">
  <div *ngIf="applications.length == 0" class="alert alert-danger text-center" role="alert">
    <h4>No applications found.</h4>
  </div>
  <table *ngIf="applications.length > 0" class="table table-striped table-hover table-responsive">
    <thead class="table-dark">
      <tr>
        <th scope="col">#</th>
        <th scope="col">Description</th>
        <th scope="col">Email</th>
        <th scope="col">Created</th>
        <th scope="col">Needed till</th>
        <th scope="col">Technology</th>
        <th scope="col">Status</th>
        <th scope="col" *ngIf="!showForm">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let a of applications">
        <th scope="row">{{a.id}}</th>
        <td>{{getDescription(a?.description)}}</td>
        <td>{{a.email}}</td>
        <td>{{toDate(a.created) | date: 'dd.MM.yyyy HH:mm'}}</td>
        <td>{{toDate(a.neededTill) | date: 'dd.MM.yyyy HH:mm'}}</td>
        <td>{{a.technology?.type}}</td>
        <td>{{a.status}}</td>
        <td class="d-flex py-auto" *ngIf="!showForm">
          <i class="btn btn-success bi bi-check2 mx-2"></i>
          <i class="btn btn-danger bi bi-x-lg mx-2"></i>
          <i class="btn btn-outline-danger bi bi-trash3-fill mx-2" (click)="emitEvent(a)"></i>
        </td>
      </tr>
    </tbody>
  </table>
</div>
