import { HttpClient, HttpResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs/internal/Observable';
import { technologyUrl } from 'src/app/constants/app.constants';

@Injectable({
  providedIn: 'root'
})
export class TechnologyService {

  constructor(private http: HttpClient) {}

  getAllTechnologies() : Observable<HttpResponse<Object>> {
    return this.http.get(technologyUrl, {observe: 'response'});
  }

  get technologyTypes() : Array<string> {
    return [
      'desktop',
      'laptop',
      'tablet',
      'phone',
    ];
  }

  get technologyRAM() : Array<number> {
    return [2, 4, 8, 16, 32, 64, 128];
  }

  get technologyCores() : Array<number> {
    return [2, 4, 8, 16, 32, 64, 128];
  }
}
